import React, { useState } from 'react';
import Axios from 'axios';
import {useHistory} from "react-router-dom";

import './profile.css';

function Profile() {
    let history = useHistory();

    const [fullname, setFullname] = useState('')
    const [email, setEmail] = useState('')
    const [username, setUsername] = useState('')
    const [pass, setPass] = useState('')
    const [cpass, setcPass] = useState('')


    const reg = () => {
        Axios.post('http://localhost:3001/reg', {
            fullname: fullname,
            email: email,
            username: username,
            password: pass,
        }).then((response) => { console.log(response); })
    };

    return (
        // <div className="container">
        //     <div className="form_container">
        //         <div className="title_conatainer">
        //             <h3>Name</h3>
        //             <h4>Email</h4>
        //             <h4>Username</h4>
        //         </div>
        //         <div className="field_container">
        //             <div className="input_container">
        //                 <label> Username </label>
        //                 <input type="text" name="uname" id="uname" onChange={(e) => { setUsername(e.target.value) }} name="uname" />
        //             </div>
        //             <div className="input_container">
        //                 <label> Password </label>
        //                 <input type="password" name="pass" id="pass" onChange={(e) => { setPass(e.target.value) }} name="pass" />
        //             </div>
        //         </div>
        //         <div className="button_container">
        //             <button type="submit" onClick={login}>Login</button>
        //         </div>
        //     </div>
        // </div>

        <div class="maincontainer">

            <div class="subcontainer">
                <div class="inter_lcontainer">
                    {/* <img src="/ml.png" height="580px" width="500px" id="ml1" /> */}
                    <img src="/logo.png" height="100px" />
                </div>

                <div class="inter_rcontainer">
                    <div class="logo">
                        {/* <img src="/logo.png" height="100px" /> */}
                    </div>

                    <h4>Edit your details below</h4>

                    <div id="register_form" class="input-fields">
                        <div class="inputf1">
                            <input type="text" name="fname" id="fname" class="input" placeholder="Full Name"
                                required onChange={(e) => { setFullname(e.target.value) }} />
                        </div>

                        <div class="inputf1">
                            <input type="text" name="email" id="email" class="input" placeholder="Email"
                                required onChange={(e) => { setEmail(e.target.value) }} />
                        </div>

                        <div class="inputf1">
                            <h5>Please enter your password to verify</h5>
                        </div>

                        <div class="inputf1">
                            <input type="password" name="pwd" id="pwd" class="input" placeholder="Password"
                                required onChange={(e) => { setPass(e.target.value) }} />
                        </div>
                    </div>

                    <div>
                        <input type="submit" value="Sign up" id="Signup" onClick={reg} />
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Profile
