import React ,{useState} from 'react';
import Axios from 'axios'
import './register.css';

function Register() {
    const [fullname,setFullname] = useState('')
    const [email, setEmail] = useState('')
    const [username,setUsername] = useState('')
    const [pass, setPass] = useState('')
    const [cpass, setcPass] = useState('')


   const reg = () => {
    Axios.post('http://localhost:3001/reg',{
        fullname: fullname,
        email : email,
        username:username,
        password:pass,
    }).then((response)=>{console.log(response);})
};
  return (
    <div className="container">
        <div className="form_container">
            <div className="title_conatainer">
                <h3>Register</h3>
            </div>
            <div className="field_container">
                <div className="input_container">
                    <label> Fullname </label>
                    <input type="text" onChange={(e) => {setFullname(e.target.value)}} name="fname" />
                </div>
                <div className="input_container">
                    <label> Email </label>
                    <input type="email" onChange={(e) => {setEmail(e.target.value)}} name="email" />
                </div>
                <div className="input_container">
                    <label> Username </label>
                    <input type="text" onChange={(e) => {setUsername(e.target.value)}} name="uname" />
                </div>
                <div className="input_container">
                    <label> Password </label>
                    <input type="password" onChange={(e) => {setPass(e.target.value)}} name="pass" />
                </div>
                <div className="input_container">
                    <label> Confirm Password </label>
                    <input type="password" onChange={(e) => {setcPass(e.target.value)}} name="cpass" />
                </div>
            </div>
            <div className="button_container">
                <button type="submit" onClick={reg}>Submit</button>    
            </div>  
        </div>
    </div>
  )
}

export default Register;
